using System;
using System.Collections.Generic;
using System.Linq;
using Books.Models;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using Newtonsoft.Json;

namespace Books.Services.Tests
{
    [TestClass]
    public class BookDataServiceTests
    {
        private readonly IEnumerable<Book> _books;
        private BookDataService _bookDataService;

        public BookDataServiceTests()
        {
            #region _books init
            _books = new List<Book>
            {
                new Book
                {
                    Id = Guid.NewGuid(),
                    Title = "В тихом омуте",
                    Authors = new []
                    {
                        new Author
                        {
                            FirstName = "Пол",
                            LastName = "Хокинс"
                        },
                        new Author
                        {
                            FirstName = "Джон",
                            LastName = "Гришем"
                        }
                    },
                    PageCount = 297,
                    Year = 2017,
                    Publisher = "Эксмо",
                    Isbn = "978-5-389-09475-8",
                    Image = "data:image/jpeg;base64,",
                    CreateDate = DateTime.Now,
                    EditDate = DateTime.Now
                },
                new Book
                {
                    Id = Guid.NewGuid(),
                    Title = "Девушка в поезде",
                    Authors = new []
                    {
                        new Author
                        {
                            FirstName = "Дэвид",
                            LastName = "Моррелл"
                        },
                        new Author
                        {
                            FirstName = "Блейк",
                            LastName = "Крауч"
                        },
                        new Author
                        {
                            FirstName = "Анжела",
                            LastName = "Марсонс"
                        }
                    },
                    PageCount = 315,
                    Year = 2013,
                    Publisher = "Азбука",
                    Isbn = "978-5-699-94314-2",
                    Image = "data:image/jpeg;base64,",
                    CreateDate = DateTime.Now,
                    EditDate = DateTime.Now
                }
            };
            #endregion
        }

        [TestMethod]
        public void GetTest()
        {
            _bookDataService = new BookDataService(_books);

            var books = _bookDataService.Get();

            Assert.AreEqual(2, books.Count());
        }

        [TestMethod]
        public void GetByIdTest()
        {
            var myBook = _books.First();

            _bookDataService = new BookDataService(_books);

            var book = _bookDataService.Get(myBook.Id);

            Assert.AreEqual(myBook, book);
        }

        [TestMethod]
        public void GetImageTest()
        {
            var myBook = _books.First();

            _bookDataService = new BookDataService(_books);

            var image = _bookDataService.GetImage(myBook.Id);

            Assert.AreEqual(myBook.Image, image);
        }

        [TestMethod]
        public void DeleteTest()
        {
            var myBook = _books.First();

            _bookDataService = new BookDataService(_books);

            _bookDataService.Delete(myBook.Id);

            var books = _bookDataService.Get();

            Assert.AreEqual(1, books.Count());
        }

        [TestMethod]
        public void CreateTest()
        {
            _bookDataService = new BookDataService(_books);

            var newBook = new Book
            {
                Id = Guid.NewGuid(),
                Title = "В тихом омуте 2",
                Authors = new[]
                    {
                        new Author
                        {
                            FirstName = "Пол",
                            LastName = "Хокинс"
                        },
                        new Author
                        {
                            FirstName = "Джон",
                            LastName = "Гришем"
                        }
                    },
                PageCount = 297,
                Year = 2017,
                Publisher = "Эксмо",
                Isbn = "978-5-389-09475-8",
                CreateDate = DateTime.Now,
                EditDate = DateTime.Now
            };

            _bookDataService.Create(newBook);

            var books = _bookDataService.Get().ToList();

            var myNewBook = books.FirstOrDefault(p => p.Id == newBook.Id);

            Assert.AreEqual(3, books.Count);
            Assert.IsNotNull(myNewBook);
            Assert.AreEqual(newBook, myNewBook);
        }

        [TestMethod]
        public void UpdateTest()
        {
            const string newTitle = "В тихом омуте 2";

            var myBook = JsonConvert.DeserializeObject<Book>(JsonConvert.SerializeObject(_books.First()));

            myBook.Title = newTitle;

            _bookDataService = new BookDataService(_books);

            _bookDataService.Update(myBook, false);

            var book = _bookDataService.Get(myBook.Id);

            Assert.AreEqual(newTitle, book.Title);
        }
    }
}
